const UserType = ({ values, status }) => {
  const [type, setType] = useState([]);

  //sets
  useEffect(() => {
    if (status) {
        setType([type, status]);
    }
  }, [type, status]);

  return (
    <div>
      <Form>
        <h2>I am a</h2>
        <div>
          <div>
            <label>Volunteer</label>
            <Field type="radio" name="volunteerUser"/>
          </div>
          <div>
            <label>Business</label>
            <Field type="radio" name="businessUser"
            checked={type.bu}/>
          </div>
        </div>
      </Form>
    </div>
  );
};

const FormikUserType = withFormik({
